import React, { useState, useEffect, useCallback } from 'react';
import api from '../config/api';
import PublicRoomView from './PublicRoomView';
import './CustomerDashboard.css';

const CustomerDashboard = ({ customer, onLogout, onShowRooms, refreshTrigger }) => {
  const [bookings, setBookings] = useState([]);
  const [bookingRequests, setBookingRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('bookings');
  const [uploadingIdProof, setUploadingIdProof] = useState(false);
  const [error, setError] = useState(null);

  const fetchCustomerData = useCallback(async (retryCount = 0) => {
    if (!customer || !customer.phoneNumber) {
      console.log('Customer or phoneNumber not available:', customer);
      setLoading(false);
      return;
    }

    // Check if we have a valid token
    const token = localStorage.getItem('customerToken');
    if (!token) {
      console.log('No customer token found, redirecting to login...');
      if (onLogout) {
        onLogout();
      }
      return;
    }

    try {
      setError(null); // Clear any previous errors
      console.log('Fetching data for customer:', customer);
      console.log('Customer phone number:', customer.phoneNumber);
      console.log('Customer token:', localStorage.getItem('customerToken'));
      console.log('All localStorage items:', {
        customerToken: localStorage.getItem('customerToken'),
        customerData: localStorage.getItem('customerData'),
        staffToken: localStorage.getItem('staffToken')
      });

      // Fetch bookings
      const bookingsResponse = await api.get('/customer/bookings', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('customerToken')}`
        }
      });
      setBookings(bookingsResponse.data || []);

      // Fetch booking requests
      const requestsResponse = await api.get(`/booking-requests/customer/${customer.phoneNumber}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('customerToken')}`
        }
      });
      console.log('Booking requests response:', requestsResponse.data);
      setBookingRequests(requestsResponse.data || []);
    } catch (error) {
      console.error('Error fetching customer data:', error);
      console.error('Error response:', error.response?.data);
      console.error('Error status:', error.response?.status);

      // Only clear authentication for specific authentication errors
      if (error.response?.status === 401 || 
          (error.response?.status === 400 && 
           (error.response?.data?.message?.includes('token') || 
            error.response?.data?.message?.includes('authentication') ||
            error.response?.data?.message?.includes('unauthorized')))) {
        console.log('Authentication error detected, clearing localStorage and redirecting to login...');
        localStorage.removeItem('customerToken');
        localStorage.removeItem('customerData');
        // The parent component should handle the redirect
        if (onLogout) {
          onLogout();
        }
      } else {
        // For other errors, just show a user-friendly message
        console.log('Non-authentication error, keeping user logged in');

        // Retry for network errors (up to 2 times)
        if (retryCount < 2 && (!error.response || error.code === 'NETWORK_ERROR' || error.message.includes('Network Error'))) {
          console.log(`Retrying fetch (attempt ${retryCount + 1}/2)...`);
          setTimeout(() => {
            fetchCustomerData(retryCount + 1);
          }, 1000 * (retryCount + 1)); // Exponential backoff
          return;
        }
      }

      setBookings([]);
      setBookingRequests([]);
    } finally {
      setLoading(false);
    }
  }, [customer, onLogout]);

  useEffect(() => {
    fetchCustomerData();
  }, [fetchCustomerData]);

  // Refresh data when refreshTrigger changes
  useEffect(() => {
    if (refreshTrigger) {
      console.log('Refresh trigger detected, refetching customer data...');
      fetchCustomerData();
    }
  }, [refreshTrigger, fetchCustomerData]);

  const previewInvoice = async (bookingId) => {
    try {
      const response = await api.get(`/invoices/${bookingId}/preview`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('customerToken')}`
        }
      });

      // Open invoice preview in a new window
      const newWindow = window.open('', '_blank');
      newWindow.document.write(response.data);
      newWindow.document.close();
    } catch (error) {
      console.error('Error previewing invoice:', error);
      console.error('Error response:', error.response?.data);
      alert('Failed to preview invoice. Please try again.');
    }
  };

  const downloadInvoice = async (bookingId) => {
    try {
      console.log('Downloading invoice for booking:', bookingId);
      const response = await api.get(`/invoices/${bookingId}/download`, {
        responseType: 'blob',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('customerToken')}`
        }
      });

      console.log('Invoice download response:', response);
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `invoice-${bookingId}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Error downloading invoice:', error);
      console.error('Error response:', error.response?.data);
      console.error('Error status:', error.response?.status);
      alert('Error downloading invoice. Please try again.');
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  const getStatusBadge = (status) => {
    const statusMap = {
      'NEW': { text: 'New', class: 'status-new' },
      'PENDING': { text: 'Pending', class: 'status-pending' },
      'CONFIRMED': { text: 'Confirmed', class: 'status-confirmed' },
      'CANCELLED': { text: 'Cancelled', class: 'status-cancelled' },
      'CHECKEDOUT': { text: 'Checked Out', class: 'status-checkedout' },
      'CHECKEDIN': { text: 'Checked In', class: 'status-checkedin' }
    };

    const statusInfo = statusMap[status] || { text: status, class: 'status-default' };
    return <span className={`status-badge ${statusInfo.class}`}>{statusInfo.text}</span>;
  };

  const getRequestStatusBadge = (status) => {
    const statusMap = {
      'PENDING': { text: 'Pending Review', class: 'status-pending' },
      'APPROVED': { text: 'Approved', class: 'status-confirmed' },
      'REJECTED': { text: 'Rejected', class: 'status-cancelled' }
    };

    const statusInfo = statusMap[status] || { text: status, class: 'status-default' };
    return <span className={`status-badge ${statusInfo.class}`}>{statusInfo.text}</span>;
  };

  const calculateTotalDues = () => {
    return bookings.reduce((total, booking) => {
      return total + (booking.dueAmount || 0);
    }, 0);
  };

  const handleIdProofUpload = async (e) => {
    setUploadingIdProof(true);
    try {
      // Customers cannot upload files to Google Drive
      alert('âŒ Access Denied\n\nCustomers cannot upload files directly to Google Drive. Please contact the caretaker or owner to upload your ID proofs.');
    } finally {
      setUploadingIdProof(false);
      e.target.value = ''; // Clear the file input
    }
  };

  const handleDeleteIdProof = async (index) => {
    if (!window.confirm('Are you sure you want to delete this ID proof?')) {
      return;
    }

    try {
      const response = await api.delete(`/customer/${customer.phoneNumber}/id-proofs/${index}`);

      if (response.data.success) {
        // Remove the URL from the customer object
        customer.idProofUrls.splice(index, 1);
        alert('ID proof deleted successfully!');
      } else {
        alert('Error deleting ID proof: ' + response.data.message);
      }
    } catch (error) {
      console.error('Error deleting ID proof:', error);
      alert('Error deleting ID proof. Please try again.');
    }
  };

  if (loading || !customer) {
    return (
      <div className="loading">
        <div className="spinner"></div>
        <p>Loading your dashboard...</p>
      </div>
    );
  }

  return (
    <div className="customer-dashboard">
      {/* Summary Cards */}
      <div className="summary-section">
        <div className="container">
          <div className="summary-cards">
            <div className="summary-card">
              <div className="card-icon">ðŸ“…</div>
              <div className="card-content">
                <h3>{bookings.length}</h3>
                <p>Total Bookings</p>
              </div>
            </div>
            <div className="summary-card">
              <div className="card-icon">ðŸ’°</div>
              <div className="card-content">
                <h3>â‚¹{calculateTotalDues()}</h3>
                <p>Pending Dues</p>
              </div>
            </div>
            <div className="summary-card">
              <div className="card-icon">ðŸ“‹</div>
              <div className="card-content">
                <h3>{bookingRequests.length}</h3>
                <p>Booking Requests</p>
              </div>
            </div>
            <div className="summary-card">
              <div className="card-icon">âœ…</div>
              <div className="card-content">
                <h3>{bookings.filter(b => b.bookingStatus === 'CONFIRMED').length}</h3>
                <p>Confirmed Bookings</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <div className="container">
          <div className="dashboard-tabs">
            <button 
              className={`tab ${activeTab === 'rooms' ? 'active' : ''}`}
              onClick={() => setActiveTab('rooms')}
            >
              View Rooms
            </button>
            <button 
              className={`tab ${activeTab === 'requests' ? 'active' : ''}`}
              onClick={() => setActiveTab('requests')}
            >
              My Requests
            </button>
            <button 
              className={`tab ${activeTab === 'bookings' ? 'active' : ''}`}
              onClick={() => setActiveTab('bookings')}
            >
              My Bookings
            </button>
            <button 
              className={`tab ${activeTab === 'profile' ? 'active' : ''}`}
              onClick={() => setActiveTab('profile')}
            >
              Profile
            </button>
          </div>

          {activeTab === 'rooms' && (
            <div className="rooms-section">
              <PublicRoomView 
                customer={customer}
                onShowAuth={() => {}} // No need for auth since customer is already logged in
              />
            </div>
          )}

          {activeTab === 'requests' && (
            <div className="requests-section">
              <div className="requests-header">
                <h2>My Booking Requests</h2>
                <button 
                  className="refresh-btn"
                  onClick={fetchCustomerData}
                  title="Refresh data"
                >
                  ðŸ”„ Refresh
                </button>
              </div>

              {bookingRequests.length === 0 ? (
                <div className="empty-state">
                  <div className="empty-icon">ðŸ“‹</div>
                  <h3>No booking requests yet</h3>
                  <p>You haven't submitted any booking requests yet. Start by browsing our rooms!</p>
                </div>
              ) : (
                <div className="requests-list">
                  {bookingRequests.map(request => (
                    <div key={request.id} className="request-card">
                      <div className="request-header">
                        <h3>Room {request.roomId}</h3>
                        {getRequestStatusBadge(request.status)}
                      </div>

                      <div className="request-details">
                        <div className="detail-row">
                          <span className="label">Check-in:</span>
                          <span className="value">{formatDate(request.checkInDate)}</span>
                        </div>
                        <div className="detail-row">
                          <span className="label">Check-out:</span>
                          <span className="value">{formatDate(request.checkOutDate)}</span>
                        </div>
                        <div className="detail-row">
                          <span className="label">Duration:</span>
                          <span className="value">{request.bookingDurationType}</span>
                        </div>
                        <div className="detail-row">
                          <span className="label">Total Amount:</span>
                          <span className="value">â‚¹{request.totalAmount}</span>
                        </div>
                        <div className="detail-row">
                          <span className="label">Submitted:</span>
                          <span className="value">{formatDate(request.createdAt)}</span>
                        </div>
                        {request.remarks && (
                          <div className="detail-row">
                            <span className="label">Remarks:</span>
                            <span className="value">{request.remarks}</span>
                          </div>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}

          {activeTab === 'bookings' && (
            <div className="bookings-section">
              <h2>My Bookings</h2>

              {bookings.length === 0 ? (
                <div className="empty-state">
                  <div className="empty-icon">ðŸ“…</div>
                  <h3>No bookings yet</h3>
                  <p>You haven't made any bookings yet. Start exploring our rooms!</p>
                </div>
              ) : (
                <div className="bookings-list">
                  {bookings.map(booking => (
                    <div key={booking.id} className="booking-card">
                      <div className="booking-header">
                        <h3>Room {booking.roomId}</h3>
                        {getStatusBadge(booking.bookingStatus)}
                      </div>

                      <div className="booking-details">
                        <div className="detail-row">
                          <span className="label">Check-in:</span>
                          <span className="value">{formatDate(booking.checkInDate)}</span>
                        </div>
                        <div className="detail-row">
                          <span className="label">Check-out:</span>
                          <span className="value">{formatDate(booking.checkOutDate)}</span>
                        </div>
                        <div className="detail-row">
                          <span className="label">Room Type:</span>
                          <span className="value">{booking.room?.roomType}</span>
                        </div>
                        <div className="detail-row">
                          <span className="label">Duration:</span>
                          <span className="value">{booking.bookingDurationType}</span>
                        </div>
                        <div className="detail-row">
                          <span className="label">Total Amount:</span>
                          <span className="value">â‚¹{booking.totalAmount}</span>
                        </div>
                        {booking.dueAmount > 0 && (
                          <div className="detail-row due">
                            <span className="label">Pending Dues:</span>
                            <span className="value">â‚¹{booking.dueAmount}</span>
                          </div>
                        )}
                      </div>

                      <div className="booking-actions">
                        <button
                          className="btn btn-secondary"
                          onClick={() => previewInvoice(booking.id)}
                          style={{ marginRight: '10px' }}
                        >
                          ðŸ‘ï¸ Preview Invoice
                        </button>
                        <button
                          className="btn btn-primary"
                          onClick={() => downloadInvoice(booking.id)}
                        >
                          ðŸ“„ Download Invoice
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}

          {activeTab === 'profile' && (
            <div className="profile-section">
              <h2>Profile Information</h2>

              <div className="profile-card">
                <div className="profile-details">
                  <div className="detail-row">
                    <span className="label">Name:</span>
                    <span className="value">{customer.name}</span>
                  </div>
                  <div className="detail-row">
                    <span className="label">Phone:</span>
                    <span className="value">{customer.phoneNumber}</span>
                  </div>
                  {customer.additionalPhoneNumber && (
                    <div className="detail-row">
                      <span className="label">Additional Phone:</span>
                      <span className="value">{customer.additionalPhoneNumber}</span>
                    </div>
                  )}
                  {customer.remarks && (
                    <div className="detail-row">
                      <span className="label">Remarks:</span>
                      <span className="value">{customer.remarks}</span>
                    </div>
                  )}
                </div>
              </div>

              {/* ID Proof Section */}
              <div className="id-proof-section">
                <h3>Photo ID Proofs</h3>
                <div className="id-proof-card">
                  {(customer.idProofUrls && customer.idProofUrls.length > 0) || customer.photoIdProofUrl ? (
                    <div className="id-proof-uploaded">
                      <div className="id-proof-status">
                        <span className="status-icon">âœ…</span>
                        <span className="status-text">
                          {customer.idProofUrls ? customer.idProofUrls.length : 1} ID Proof(s) Uploaded
                        </span>
                      </div>

                      {/* Display all ID proofs */}
                      <div className="id-proofs-list">
                        {/* Legacy single ID proof */}
                        {customer.photoIdProofUrl && (
                          <div className="id-proof-item">
                            <div className="id-proof-info">
                              <span className="id-proof-name">ðŸ“„ ID Proof Document</span>
                              <span className="id-proof-type">Legacy Upload</span>
                            </div>
                            <div className="id-proof-actions">
                              <a 
                                href={customer.photoIdProofUrl} 
                                target="_blank" 
                                rel="noopener noreferrer"
                                className="btn btn-sm btn-secondary"
                              >
                                ðŸ‘ï¸ View
                              </a>
                            </div>
                          </div>
                        )}

                        {/* Multiple ID proofs */}
                        {customer.idProofUrls && customer.idProofUrls.map((url, index) => (
                          <div key={index} className="id-proof-item">
                            <div className="id-proof-info">
                              <span className="id-proof-name">ðŸ“„ ID Proof #{index + 1}</span>
                              <span className="id-proof-type">Document</span>
                            </div>
                            <div className="id-proof-actions">
                              <a 
                                href={url} 
                                target="_blank" 
                                rel="noopener noreferrer"
                                className="btn btn-sm btn-secondary"
                              >
                                ðŸ‘ï¸ View
                              </a>
                              <button 
                                onClick={() => handleDeleteIdProof(index)}
                                className="btn btn-sm btn-danger"
                              >
                                ðŸ—‘ï¸ Delete
                              </button>
                            </div>
                          </div>
                        ))}
                      </div>

                      <div className="id-proof-actions">
                        <label className="btn btn-primary">
                          {uploadingIdProof ? 'â³ Uploading...' : 'ðŸ“¤ Add More ID Proofs'}
                          <input
                            type="file"
                            accept="image/*,.pdf"
                            multiple
                            onChange={handleIdProofUpload}
                            style={{ display: 'none' }}
                            disabled={uploadingIdProof}
                          />
                        </label>
                      </div>
                    </div>
                  ) : (
                    <div className="id-proof-missing">
                      <div className="id-proof-status">
                        <span className="status-icon">âš ï¸</span>
                        <span className="status-text">Action Required: Upload ID Proofs</span>
                      </div>
                      <div className="id-proof-description">
                        <p>Please upload clear photos of your government-issued ID documents (Aadhaar, PAN, Driving License, etc.) for verification purposes. You can upload multiple documents.</p>
                      </div>
                      <div className="id-proof-actions">
                        <label className="btn btn-primary">
                          {uploadingIdProof ? 'â³ Uploading...' : 'ðŸ“¤ Upload ID Proofs'}
                          <input
                            type="file"
                            accept="image/*,.pdf"
                            multiple
                            onChange={handleIdProofUpload}
                            style={{ display: 'none' }}
                            disabled={uploadingIdProof}
                          />
                        </label>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CustomerDashboard;
