import React, { useState, useEffect } from 'react';
import api from '../config/api';
import './BookingRequestsScreen.css';

const BookingRequestsScreen = () => {
  const [bookingRequests, setBookingRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [processing, setProcessing] = useState(null);

  useEffect(() => {
    fetchBookingRequests();
  }, []);

  const fetchBookingRequests = async () => {
    try {
      const response = await api.get('/booking-requests');
      setBookingRequests(response.data);
    } catch (error) {
      console.error('Error fetching booking requests:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRequestAction = async (requestId, action) => {
    setProcessing(requestId);
    try {
      await api.put(`/booking-requests/${requestId}/${action}`);

      // Update the local state
      setBookingRequests(prev => 
        prev.map(request => 
          request.id === requestId 
            ? { ...request, status: action.toUpperCase() }
            : request
        )
      );

      alert(`Booking request ${action}d successfully!`);
    } catch (error) {
      console.error(`Error ${action}ing booking request:`, error);
      alert(`Failed to ${action} booking request. Please try again.`);
    } finally {
      setProcessing(null);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  const getStatusBadge = (status) => {
    const statusMap = {
      'PENDING': { text: 'Pending Review', class: 'status-pending' },
      'APPROVED': { text: 'Approved', class: 'status-approved' },
      'REJECTED': { text: 'Rejected', class: 'status-rejected' }
    };

    const statusInfo = statusMap[status] || { text: status, class: 'status-default' };
    return <span className={`status-badge ${statusInfo.class}`}>{statusInfo.text}</span>;
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
        <p>Loading booking requests...</p>
      </div>
    );
  }

  return (
    <div className="booking-requests-screen">
      <div className="screen-header">
        <h1>Booking Requests</h1>
        <p>Review and manage customer booking requests</p>
      </div>

      {bookingRequests.length === 0 ? (
        <div className="empty-state">
          <div className="empty-icon">üìã</div>
          <h3>No booking requests</h3>
          <p>There are no pending booking requests at the moment.</p>
        </div>
      ) : (
        <div className="requests-grid">
          {bookingRequests.map(request => (
            <div key={request.id} className="request-card">
              <div className="request-header">
                <div className="request-info">
                  <h3>Room {request.room?.roomNumber}</h3>
                  <p className="customer-name">{request.customerName}</p>
                  <p className="customer-phone">{request.customerPhone}</p>
                </div>
                {getStatusBadge(request.status)}
              </div>

              <div className="request-details">
                <div className="detail-row">
                  <span className="label">Check-in:</span>
                  <span className="value">{formatDate(request.checkInDate)}</span>
                </div>
                <div className="detail-row">
                  <span className="label">Check-out:</span>
                  <span className="value">{formatDate(request.checkOutDate)}</span>
                </div>
                <div className="detail-row">
                  <span className="label">Room Type:</span>
                  <span className="value">{request.room?.roomType}</span>
                </div>
                <div className="detail-row">
                  <span className="label">Bathroom Type:</span>
                  <span className="value">{request.room?.bathroomType}</span>
                </div>
                <div className="detail-row">
                  <span className="label">Duration:</span>
                  <span className="value">{request.bookingDurationType}</span>
                </div>
                <div className="detail-row">
                  <span className="label">Daily Cost:</span>
                  <span className="value">‚Çπ{request.dailyCost}</span>
                </div>
                <div className="detail-row">
                  <span className="label">Monthly Cost:</span>
                  <span className="value">‚Çπ{request.monthlyCost}</span>
                </div>
                <div className="detail-row">
                  <span className="label">Early Check-in:</span>
                  <span className="value">‚Çπ{request.earlyCheckinCost}</span>
                </div>
                <div className="detail-row total">
                  <span className="label">Total Amount:</span>
                  <span className="value">‚Çπ{request.totalAmount}</span>
                </div>
                <div className="detail-row">
                  <span className="label">Submitted:</span>
                  <span className="value">{formatDate(request.createdAt)}</span>
                </div>
              </div>

              {request.status === 'PENDING' && (
                <div className="request-actions">
                  <button 
                    className="btn btn-approve"
                    onClick={() => handleRequestAction(request.id, 'approve')}
                    disabled={processing === request.id}
                  >
                    {processing === request.id ? '‚è≥ Processing...' : '‚úÖ Approve'}
                  </button>
                  <button 
                    className="btn btn-reject"
                    onClick={() => handleRequestAction(request.id, 'reject')}
                    disabled={processing === request.id}
                  >
                    {processing === request.id ? '‚è≥ Processing...' : '‚ùå Reject'}
                  </button>
                </div>
              )}

              {request.status !== 'PENDING' && (
                <div className="request-status">
                  <p className="status-message">
                    This request has been {request.status.toLowerCase()}.
                  </p>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default BookingRequestsScreen;
